using System.Net;using System.Net.Mail;using HospitalApi.LoginServices;namespace Application.Contracts.Services;public class EmailAppService : IEmailAppService{    public void SendEmail(string recipientEmail, string subject, string htmlBody)    {        string senderEmail = "Academy@codedonor.in";        string senderPassword = "fjCmXdM7UxXk";        SmtpClient smtpClient = new SmtpClient("smtppro.zoho.in")        {            Port = 587,            Credentials = new NetworkCredential(senderEmail, senderPassword),            EnableSsl = true,        };        MailMessage mailMessage = new MailMessage(senderEmail, recipientEmail)        {            Subject = subject,            IsBodyHtml = true,            Body = htmlBody        };        try        {            smtpClient.Send(mailMessage);            Console.WriteLine("Email sent successfully");        }        catch (Exception ex)        {            Console.WriteLine("Email could not be sent: " + ex.Message);        }    }}